/*****************************************************************************
 结构名    : DPU_ODC_CTRL_UNION
 结构说明  : ODC_CTRL 寄存器结构定义。地址偏移量:0x0000，初值:0x00000042，宽度:32
 寄存器说明: 模块控制信号
*****************************************************************************/
#ifndef __SOC_H_FOR_ASM__
typedef union
{
    unsigned int      value;
    struct
    {
        unsigned int  mbc_bypass       : 1;  /* bit[0]    : 补偿开关：
                                                            1'b1 :补偿bypass，输出图像bypass输入图像，不需要state lut输入
                                                            1'b0 :补偿使能，输出图像经过补偿后输出，需要state lut输入 */
        unsigned int  pre_frame_src    : 1;  /* bit[1]    : state out 存储源选择：
                                                            1'b1: 使用当前帧原灰阶更新预存像素值，不需要state lut输入，不进行state计算。
                                                            1'b0: 使用state计算结果更新预存像素值，需要state lut输入 */
        unsigned int  down_sample_mode : 2;  /* bit[2-3]  : 下采样模式：
                                                            0: AVG
                                                            1: MAX
                                                            2: MIN
                                                            【notes】: 1x1 block模式下不要配置AVG */
        unsigned int  save_bit_depth   : 1;  /* bit[4]    : 下采样输出存储时的位宽：
                                                            1'b1: 10bit
                                                            1'b0: 8bit
                                                            【notes】: 也是lut 回读时的位宽 */
        unsigned int  panel_type       : 2;  /* bit[5-6]  : 显示屏的设定，及数据排布方式。
                                                            0: SPR-Delta RGB,reserve
                                                            1: SPR-RGBG
                                                            2: Real RGB */
        unsigned int  reserved_0       : 1;  /* bit[7-7]  : 保留 */
        unsigned int  in_lut_layer_id  : 5;  /* bit[8-12] : 给ODC的输入state lut分配的layer_id标识 */
        unsigned int  reserved_1       : 3;  /* bit[13-15]: 保留 */
        unsigned int  lut_info_os      : 8;  /* bit[16-23]: '一次发送lut_info个数的上限
                                                            【约束】:
                                                            0 < lut_info_os <= 31 */
        unsigned int  lut_info_left    : 8;  /* bit[24-31]: 发送lut_info时，剩余lut_data行数
                                                            【约束】:
                                                            0 < lut_info_left < lut_info_os */
    } reg;
} DPU_ODC_CTRL_UNION;
#endif


